import React, { useCallback, useState } from "react";
import AppLayout from "../components/Layouts/AppLayout";
import Head from "next/head";
import { Form, Input, Checkbox, Button } from "antd";
import useInput from "../hooks/useInput";
import styled from "styled-components";

const ErrorMessage = styled.div`
    color: red;
`;

const SignUp = () => {
    const [id, onChangeId] = useInput("");
    const [nickname, onChangeNickname] = useInput("");
    const [password, onChangePassword] = useInput("");

    const [passwordCheck, setPasswordCheck] = useState("");
    const [passwordError, setPasswordError] = useState(false);
    const onChangePasswordCheck = useCallback(
        (e) => {
            setPasswordCheck(e.target.value); // 여기까진 동일하나
            setPasswordError(e.target.value !== password); // 이게 추가되기 때문에 커스텀 훅 사용 불가
        },
        [password],
    );

    const onSubmit = useCallback(() => {}, []);

    return (
        <>
            <Head>
                {/* _app.js에 title 있지만 여기서 오버라이딩 */}
                <title>회원가입 | jinho</title>
            </Head>
            <AppLayout>
                <Form onFinish={onSubmit}>
                    <div>
                        <label htmlFor="user-id">아이디</label>
                        <br />
                        <Input
                            name="user-id"
                            value={id}
                            required
                            onChange={onChangeId}
                        ></Input>
                    </div>
                    <div>
                        <label htmlFor="user-nickname">닉네임</label>
                        <br />
                        <Input
                            name="user-nickname"
                            value={nickname}
                            required
                            onChange={onChangeNickname}
                        ></Input>
                    </div>
                    <div>
                        <label htmlFor="user-password">비밀번호</label>
                        <br />
                        <Input
                            name="user-password"
                            value={password}
                            required
                            onChange={onChangePassword}
                            type="password"
                        ></Input>
                    </div>
                    <div>
                        <label htmlFor="user-password-check">
                            비밀번호체크
                        </label>
                        <br />
                        <Input
                            name="user-password-check"
                            value={passwordCheck}
                            required
                            onChange={onChangePasswordCheck}
                            type="password"
                        ></Input>
                    </div>
                    <Checkbox
                        name="user-term"
                        checked={term}
                        onChange={onCahngeTerm}
                    >
                        약관동의
                    </Checkbox>

                    {passwordError && (
                        <ErrorMessage>
                            비밀번호가 일치하지 않습니다.
                        </ErrorMessage>
                    )}
                </Form>
            </AppLayout>
        </>
    );
};

export default SignUp;
