const rootReducer = combineReducers({
    index: (state = {}, action) => {
        switch (action.type) {
            case HYDRATE:
                console.log("HYDRATE", action);
                return action.payload;
            default:
                return state;
        }
    },
    user,
    post,
});

export default rootReducer;
