    user: {
        isLoggedIn: false,
        user: null,
        signUpData: {},
        loginData: {},
    },
    post: {
        mainPosts: [],
    },
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case HYDRATE:
            console.log("HYDRATE", action);
            return action.payload;
        default: {
            return state;
            // const combinedReducer = combineReducers({
