import React from "react";
import { Card, Popover, Button, Avatar } from "antd";
import {
    RetweetOutlined,
    HeartOutlined,
    MessageOutlined,
    EllipsisOutlined,
} from "@ant-design/icons";
import ButtonGroup from "antd/lib/button/button-group";
import { useSelector } from "react-redux";

const PostCard = ({ post }) => {
    const { me } = useSelector((state) => state.user);
    const id = me?.id;

    return (
        <div>
            <Card
                // cover={
                //     post.Images[0] && (
                //         <PostImages images={post.Images}></PostImages>
                //     )
                // }
                action={[
                    <RetweetOutlined key="retweet"></RetweetOutlined>,
                    <HeartOutlined key="heart"></HeartOutlined>,
                    <MessageOutlined key="comment"></MessageOutlined>,
                    <Popover
                        key="more"
                        content={
                            <Button.Group>
                                {id && post.User.id === id ? (
                                    <>
                                        <Button type="primary">수정</Button>
                                        <Button type="danger">삭제</Button>
                                    </>
                                ) : (
                                    <Button>신고</Button>
                                )}
                            </Button.Group>
                        }
                    >
                        <EllipsisOutlined></EllipsisOutlined>
                    </Popover>,
                ]}
            >
                <Card.Meta
                    avartar={<Avatar>{post.User.nickname}</Avatar>}
                    description={post.content}
                    title={post.User.nickname}
                ></Card.Meta>
            </Card>
        </div>
    );
};

export default React.memo(PostCard);
